BitmapText.InitCommand:
%function(self) 
			self:shadowlength(0);
			self:zoom(1.5);
			self:vertalign('top');
			self:y(-80);
			if not GetPref('EventMode') then 
				self:settext('SONG ' .. table.getn(AllScores)+1) 
				-- self:y(-20);
			elseif IsTimedSet() then 
				self:settext('event') end 
				-- self:y(-20);
			end
